INSERT INTO `destoon_article_24` VALUES ('4', '229', '0', '5', '中国石油天然气集团公司', '', '0', '', '中国石油天然气集团公司是一家集油气勘探开发、炼油化工、油品销售、油气储运、石油贸易、工程技术服务和石油装备制造于一体的综合性能源公司。', '中国石油天然气集团公司    油气勘探开发  炼油化工   石油贸易', '中国石油天然气集团公司,中国石油天然气集团公司,,,,油气勘探开发,,炼油化工,,,石油贸易,油企百科', '', '', '百度百科', '', '', '11', 'http://www.ogedata.com/file/upload/201504/13/11-38-06-96-21.jpg', 'yangfan2015', '1428896246', 'yangfan2015', '1428897389', '220.166.254.205', '', '3', '0', 'show-4.html', '', '');
