INSERT INTO `destoon_article_24` VALUES ('6', '226', '0', '0', '中石油集团公司总会计师、党组成员：刘跃珍', '', '0', '', '刘跃珍，男，汉族，中共党员。1961年9月出生于湖北武穴，研究生学历，硕士学位，研究员级高级会计师，在财会行业拥有逾30年的工', '刘跃珍', '中石油集团公司总会计师、党组成员：刘跃珍,刘跃珍,人物事件', '', '', '油气设备数据网', 'http://www.ogedata.com/', '', '0', '', 'ldc2014', '1428910168', 'ldc2014', '1428910985', '220.166.254.205', '', '3', '0', 'show-6.html', '', '');
